<?php/*------------------------------------------------------------Developer: Bryan Houghton IIFor: Symphony Style LLCVersion: 0.1Class Name: CSVParser.phpCreated: 26 March 2013Last Modified: 26 March 2013------------------------------------------------------------*/class CSVParser{	public function __construct(){		//Do nothing    }		public function parseCSVFile($file_name){			try{					$handle = fopen($file_name,'r');			$fields = fgetcsv($handle);			$y = 0;			while($data = fgetcsv($handle))			{				$x = 0;				foreach($data as $value)				{				   $items[$y][$fields[$x]] = $value;				   $x++;				}				 $y++;			}			return $items;					}catch(Exception $e){					echo $e->getMessage();			}		}}?>